import Container from "react-bootstrap/esm/Container";
import { Header } from "./Header";
import { Form, Col, Row, Button, Alert } from "react-bootstrap";
import { useState } from "react";
import { saveStudent, saveAdmin } from "../services/userApis"; // Import the necessary API functions

export function SignUpForm() {
  let [formData, setFormdata] = useState({ name: "", email: "", password: "" });
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [userType, setUserType] = useState("user"); // Default user type to "user"

  const handleChange = (e) => {
    setFormdata({ ...formData, [e.target.name]: e.target.value });
  };

  const handleRadioChange = (e) => {
    setUserType(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (userType === "user") {
        await (formData); // Call the saveStudent API function for user type
      } else if (userType === "admin") {
        await saveAdmin(formData); // Call the saveAdmin API function for admin type
      }

      // Clear the form data after saving
      setFormdata({ name: "", email: "", password: "" });
      setIsSubmitted(true);

      // After register click, the message should be displayed for only 1.5 secs
      setTimeout(() => {
        setIsSubmitted(false);
      }, 1500);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <Container>
      <Header text="Register Here"></Header>
      <Form onSubmit={handleSubmit}>
        {/* ... your form fields */}
        <Form.Check
          type="radio"
          label="User"
          name="user-type"
          value="user"
          checked={userType === "user"}
          onChange={handleRadioChange}
        />
        <Form.Check
          type="radio"
          label="Admin"
          name="user-type"
          value="admin"
          checked={userType === "admin"}
          onChange={handleRadioChange}
        />
        <Button variant="primary" type="submit">
          Submit
        </Button>
      </Form>
      {/* ... your alert component */}
    </Container>
  );
}
