import Container from "react-bootstrap/esm/Container";
import { Header } from "./Header";
import { Form, Col, Row, Button, Alert } from "react-bootstrap"; // Import Form from react-bootstrap
import { useState } from "react";
import { signUpAdmin, signUpUser } from "../services/userApis";

export function SignUpForm() {
  let [formData, setFormdata] = useState({ name: "", email: "", password: "" });
  const [userType, setUserType] = useState("user");
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleChange = (e) => {
    setFormdata({ ...formData, [e.target.name]: e.target.value });
  };

  const handleRadioChange = (e) => {
    setUserType(e.target.value);
  };

  const validateForm = () => {
    // Basic validation for name, email, and password
    if (
      !formData.name.trim() ||
      !formData.email.trim() ||
      !formData.password.trim() ||
      formData.email.indexOf('@') === -1
    ) {
      // Show an alert or update the UI to indicate validation error
      alert("Please fill in all fields and enter a valid email");
      return false;
    }

    // Strong password check
    const strongPasswordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
    if (!strongPasswordRegex.test(formData.password)) {
      // Show an alert or update the UI to indicate validation error
      alert("Please enter a strong password");
      return false;
    }

    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return; // Stop form submission if validation fails
    }

    try {
      if (userType === "user") {
        await signUpUser(formData);
      } else if (userType === "admin") {
        await signUpAdmin(formData);
      }

      // Clear the form data after saving
      setFormdata({ name: "", email: "", password: "" });
      setIsSubmitted(true);

      // After register click, the message should be displayed for only 1.5 secs
      setTimeout(() => {
        setIsSubmitted(false);
      }, 1500);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <Container>
      <Header text="Register Here"></Header>
      <Form onSubmit={handleSubmit}>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Name</Form.Label>
          <Form.Control type="text" placeholder="Enter Name" name="name" value={isSubmitted ? formData.roll : null} onKeyUp={handleChange} />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Email</Form.Label>
          <Form.Control type="text" placeholder="Enter email" name="email" value={isSubmitted ? formData.name : null} onKeyUp={handleChange} />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control type="text" placeholder="Enter password" name="password" value={isSubmitted ? formData.marks : null} onKeyUp={handleChange} />
        </Form.Group>

        <Form.Check
          type="radio"
          label="User"
          name="user-type"
          value="user"
          checked={userType === "user"}
          onChange={handleRadioChange}
        />
        <Form.Check
          type="radio"
          label="Admin"
          name="user-type"
          value="admin"
          checked={userType === "admin"}
          onChange={handleRadioChange}
        />

        <Button variant="primary" type="submit">
          Submit
        </Button>
      </Form>
      <Row className="mt-3">
        <Col lg={4}>
          {isSubmitted ? <Alert variant="success">User Registered</Alert> : null}
        </Col>
      </Row>
    </Container>
  );
}
