import Container from "react-bootstrap/esm/Container";
import { Header } from "./Header";
import { Form, Col, Row ,Button, Alert} from "react-bootstrap"; // Import Form from react-bootstrap
import { useState } from "react";
import { saveStudent } from "../services/fetchStudents";

export function SignUpForm() {
   let[formData,setFormdata] =useState({name:"",email:"",password:""});
   const [isSubmitted,setIsSubmitted]=useState(false);
   const  handleChange=(e)=>{
    setFormdata({...formData,[e.target.name]:e.target.value})
   }
   const handleSubmit=async(e)=>{
    e.preventDefault();
    console.log(formData,"DFDFFDF")
    try {
      const response=await saveStudent(formData);
      //clear the form data after saving
      setFormdata({roll:"",name:"",marks:"",gender:""});
      setIsSubmitted(true);
      //after register click the message should be displayed for only 1.5 secs 
      setTimeout(()=>{
        setIsSubmitted(false);
      },1000);

      console.log(response.message);
    } catch (error) {
      console.log(error);
    }

   }
  return (
    <Container>
      <Header text="Register Student Here..."></Header>
      <Form onSubmit={handleSubmit}>
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Name</Form.Label>
        <Form.Control type="text" placeholder="Enter Roll" name="roll"  value={isSubmitted?formData.roll:null} onKeyUp={handleChange}/>

      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Email</Form.Label>
        <Form.Control type="text" placeholder="Enter Name" name="name" value={isSubmitted?formData.name:null} onKeyUp={handleChange}/>
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Password</Form.Label>
        <Form.Control type="text" placeholder="Enter Marks" name="marks" value={isSubmitted?formData.marks:null} onKeyUp={handleChange}/>
      </Form.Group>
      <Form.Check

            type="radio"
            label="Male"
            name="gender"
            value="male"
            onChange={handleChange}

          />
          <Form.Check

            type="radio"
            label="Female"
            name="user-type"
            value="female"
            onChange={handleChange}
          />
      <Button variant="primary" type="submit" >
        Submit
      </Button>
    </Form>
    <Row className="mt-3">
      <Col lg={4}>
        {isSubmitted?<Alert variant="success" >Student Registered</Alert>:null}
      </Col>
    </Row>
    </Container>
  );
}
